import 'package:flutter/material.dart';\nimport '../widgets/tile_product_card.dart';\nimport '../models/tile_product.dart';\n\nclass LivingRoomTilesPage extends StatefulWidget {\n  const LivingRoomTilesPage({super.key});\n\n  @override\n  State<LivingRoomTilesPage> createState() => _LivingRoomTilesPageState();\n}\n\nclass _LivingRoomTilesPageState extends State<LivingRoomTilesPage> {\n  String selectedFilter = 'All';\n  final List<String> filters = ['All', 'Ceramic', 'Porcelain', 'Marble', 'Wood Look', 'Stone Look'];\n\n  @override\n  Widget build(BuildContext context) {\n    final filteredProducts = selectedFilter == 'All'\n        ? TileProduct.sampleProducts\n        : TileProduct.sampleProducts.where((p) => p.category == selectedFilter).toList();\n\n    return Scaffold(\n      appBar: AppBar(\n        title: const Text('Living Room Tiles'),\n        backgroundColor: Theme.of(context).colorScheme.inversePrimary,\n        actions: [\n          IconButton(\n            icon: const Icon(Icons.search),\n            onPressed: () {},\n          ),\n          IconButton(\n            icon: const Icon(Icons.shopping_cart_outlined),\n            onPressed: () {},\n          ),\n        ],\n      ),\n      body: SingleChildScrollView(\n        child: Column(\n          crossAxisAlignment: CrossAxisAlignment.start,\n          children: [\n            // Hero Banner Section\n            Container(\n              width: double.infinity,\n              height: 250,\n              decoration: BoxDecoration(\n                gradient: LinearGradient(\n                  colors: [Colors.teal.shade700, Colors.teal.shade300],\n                  begin: Alignment.topLeft,\n                  end: Alignment.bottomRight,\n                ),\n              ),\n              child: Stack(\n                children: [\n                  Positioned(\n                    right: 20,\n                    bottom: 20,\n                    child: Icon(\n                      Icons.grid_view_rounded,\n                      size: 120,\n                      color: Colors.white.withOpacity(0.2),\n                    ),\n                  ),\n                  Padding(\n                    padding: const EdgeInsets.all(24.0),\n                    child: Column(\n                      crossAxisAlignment: CrossAxisAlignment.start,\n                      mainAxisAlignment: MainAxisAlignment.center,\n                      children: [\n                        const Text(\n                          'Living Room Tiles',\n                          style: TextStyle(\n                            fontSize: 32,\n                            fontWeight: FontWeight.bold,\n                            color: Colors.white,\n                          ),\n                        ),\n                        const SizedBox(height: 12),\n                        const Text(\n                          'Transform Your Space with Premium Quality',\n                          style: TextStyle(\n                            fontSize: 16,\n                            color: Colors.white,\n                          ),\n                        ),\n                        const SizedBox(height: 20),\n                        ElevatedButton.icon(\n                          onPressed: () {},\n                          icon: const Icon(Icons.local_offer),\n                          label: const Text('View Special Offers'),\n                          style: ElevatedButton.styleFrom(\n                            backgroundColor: Colors.white,\n                            foregroundColor: Colors.teal,\n                            padding: const EdgeInsets.symmetric(horizontal: 20, vertical: 12),\n                          ),\n                        ),\n                      ],\n                    ),\n                  ),\n                ],\n              ),\n            ),\n\n            // Category Description\n            Padding(\n              padding: const EdgeInsets.all(20.0),\n              child: Column(\n                crossAxisAlignment: CrossAxisAlignment.start,\n                children: [\n                  const Text(\n                    'Elevate Your Living Room',\n                    style: TextStyle(\n                      fontSize: 24,\n                      fontWeight: FontWeight.bold,\n                    ),\n                  ),\n                  const SizedBox(height: 12),\n                  Text(\n                    'Discover our exquisite collection of living room tiles that combine elegance, durability, and style. Whether you\'re looking for modern porcelain, classic marble, or contemporary wood-look tiles, we have the perfect flooring solution to match your aesthetic vision and lifestyle needs.',\n                    style: TextStyle(\n                      fontSize: 15,\n                      color: Colors.grey[700],\n                      height: 1.5,\n                    ),\n                  ),\n                ],\n              ),\n            ),\n\n            // Key Features Section\n            Container(\n              margin: const EdgeInsets.symmetric(horizontal: 20, vertical: 10),\n              padding: const EdgeInsets.all(20),\n              decoration: BoxDecoration(\n                color: Colors.teal.shade50,\n                borderRadius: BorderRadius.circular(12),\n              ),\n              child: Column(\n                crossAxisAlignment: CrossAxisAlignment.start,\n                children: [\n                  const Text(\n                    'Why Choose Our Living Room Tiles?',\n                    style: TextStyle(\n                      fontSize: 18,\n                      fontWeight: FontWeight.bold,\n                    ),\n                  ),\n                  const SizedBox(height: 16),\n                  _buildFeatureItem(Icons.diamond, 'Premium Quality', 'Durable materials built to last'),\n                  _buildFeatureItem(Icons.water_drop, 'Easy to Clean', 'Stain and moisture resistant'),\n                  _buildFeatureItem(Icons.palette, 'Stylish Designs', 'Wide variety of patterns and colors'),\n                  _buildFeatureItem(Icons.verified, 'Warranty Protected', '10-year manufacturer warranty'),\n                ],\n              ),\n            ),\n\n            // Filter Chips\n            Padding(\n              padding: const EdgeInsets.symmetric(horizontal: 20, vertical: 16),\n              child: Column(\n                crossAxisAlignment: CrossAxisAlignment.start,\n                children: [\n                  const Text(\n                    'Shop by Type',\n                    style: TextStyle(\n                      fontSize: 18,\n                      fontWeight: FontWeight.bold,\n                    ),\n                  ),\n                  const SizedBox(height: 12),\n                  Wrap(\n                    spacing: 8,\n                    children: filters.map((filter) {\n                      final isSelected = selectedFilter == filter;\n                      return FilterChip(\n                        label: Text(filter),\n                        selected: isSelected,\n                        onSelected: (selected) {\n                          setState(() {\n                            selectedFilter = filter;\n                          });\n                        },\n                        backgroundColor: Colors.white,\n                        selectedColor: Colors.teal.shade100,\n                        checkmarkColor: Colors.teal,\n                        side: BorderSide(\n                          color: isSelected ? Colors.teal : Colors.grey.shade300,\n                        ),\n                      );\n                    }).toList(),\n                  ),\n                ],\n              ),\n            ),\n\n            // Products Grid\n            Padding(\n              padding: const EdgeInsets.symmetric(horizontal: 20),\n              child: Column(\n                crossAxisAlignment: CrossAxisAlignment.start,\n                children: [\n                  Row(\n                    mainAxisAlignment: MainAxisAlignment.spaceBetween,\n                    children: [\n                      Text(\n                        '${filteredProducts.length} Products',\n                        style: const TextStyle(\n                          fontSize: 16,\n                          fontWeight: FontWeight.w600,\n                        ),\n                      ),\n                      TextButton.icon(\n                        onPressed: () {},\n                        icon: const Icon(Icons.sort, size: 18),\n                        label: const Text('Sort by'),\n                      ),\n                    ],\n                  ),\n                  const SizedBox(height: 12),\n                  GridView.builder(\n                    shrinkWrap: true,\n                    physics: const NeverScrollableScrollPhysics(),\n                    gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(\n                      crossAxisCount: 2,\n                      crossAxisSpacing: 16,\n                      mainAxisSpacing: 16,\n                      childAspectRatio: 0.7,\n                    ),\n                    itemCount: filteredProducts.length,\n                    itemBuilder: (context, index) {\n                      return TileProductCard(product: filteredProducts[index]);\n                    },\n                  ),\n                ],\n              ),\n            ),\n\n            // Bottom Info Section\n            Container(\n              margin: const EdgeInsets.all(20),\n              padding: const EdgeInsets.all(20),\n              decoration: BoxDecoration(\n                color: Colors.grey.shade100,\n                borderRadius: BorderRadius.circular(12),\n              ),\n              child: Column(\n                crossAxisAlignment: CrossAxisAlignment.start,\n                children: [\n                  const Text(\n                    'Installation & Care Guide',\n                    style: TextStyle(\n                      fontSize: 18,\n                      fontWeight: FontWeight.bold,\n                    ),\n                  ),\n                  const SizedBox(height: 12),\n                  Text(\n                    'Our living room tiles are designed for easy installation and minimal maintenance. Each product comes with detailed installation instructions and care guidelines. For professional installation services, contact our team.',\n                    style: TextStyle(\n                      fontSize: 14,\n                      color: Colors.grey[700],\n                      height: 1.5,\n                    ),\n                  ),\n                  const SizedBox(height: 16),\n                  ElevatedButton.icon(\n                    onPressed: () {},\n                    icon: const Icon(Icons.contact_support),\n                    label: const Text('Contact Support'),\n                    style: ElevatedButton.styleFrom(\n                      backgroundColor: Colors.teal,\n                      foregroundColor: Colors.white,\n                    ),\n                  ),\n                ],\n              ),\n            ),\n          ],\n        ),\n      ),\n    );\n  }\n\n  Widget _buildFeatureItem(IconData icon, String title, String description) {\n    return Padding(\n      padding: const EdgeInsets.only(bottom: 12),\n      child: Row(\n        children: [\n          Icon(icon, color: Colors.teal, size: 24),\n          const SizedBox(width: 12),\n          Expanded(\n            child: Column(\n              crossAxisAlignment: CrossAxisAlignment.start,\n              children: [\n                Text(\n                  title,\n                  style: const TextStyle(\n                    fontWeight: FontWeight.w600,\n                    fontSize: 15,\n                  ),\n                ),\n                Text(\n                  description,\n                  style: TextStyle(\n                    fontSize: 13,\n                    color: Colors.grey[600],\n                  ),\n                ),\n              ],\n            ),\n          ),\n        ],\n      ),\n    );\n  }\n}\n