import 'package:flutter/material.dart';\nimport '../models/tile_product.dart';\n\nclass TileProductCard extends StatelessWidget {\n  final TileProduct product;\n\n  const TileProductCard({super.key, required this.product});\n\n  @override\n  Widget build(BuildContext context) {\n    return Card(\n      elevation: 2,\n      shape: RoundedRectangleBorder(\n        borderRadius: BorderRadius.circular(12),\n      ),\n      child: InkWell(\n        onTap: () {\n          // Navigate to product detail page (to be implemented)\n          ScaffoldMessenger.of(context).showSnackBar(\n            SnackBar(\n              content: Text('View details: ${product.name}'),\n              duration: const Duration(seconds: 1),\n            ),\n          );\n        },\n        borderRadius: BorderRadius.circular(12),\n        child: Column(\n          crossAxisAlignment: CrossAxisAlignment.start,\n          children: [\n            // Product Image\n            Stack(\n              children: [\n                ClipRRect(\n                  borderRadius: const BorderRadius.vertical(top: Radius.circular(12)),\n                  child: Container(\n                    height: 140,\n                    width: double.infinity,\n                    color: Colors.grey[200],\n                    child: Image.network(\n                      product.imageUrl,\n                      fit: BoxFit.cover,\n                      errorBuilder: (context, error, stackTrace) {\n                        return Center(\n                          child: Icon(\n                            Icons.grid_view_rounded,\n                            size: 48,\n                            color: Colors.grey[400],\n                          ),\n                        );\n                      },\n                    ),\n                  ),\n                ),\n                // Stock Badge\n                if (!product.inStock)\n                  Positioned(\n                    top: 8,\n                    right: 8,\n                    child: Container(\n                      padding: const EdgeInsets.symmetric(horizontal: 8, vertical: 4),\n                      decoration: BoxDecoration(\n                        color: Colors.red,\n                        borderRadius: BorderRadius.circular(4),\n                      ),\n                      child: const Text(\n                        'Out of Stock',\n                        style: TextStyle(\n                          color: Colors.white,\n                          fontSize: 10,\n                          fontWeight: FontWeight.bold,\n                        ),\n                      ),\n                    ),\n                  ),\n                // Favorite Button\n                Positioned(\n                  top: 8,\n                  left: 8,\n                  child: Container(\n                    decoration: BoxDecoration(\n                      color: Colors.white,\n                      shape: BoxShape.circle,\n                      boxShadow: [\n                        BoxShadow(\n                          color: Colors.black.withOpacity(0.1),\n                          blurRadius: 4,\n                        ),\n                      ],\n                    ),\n                    child: IconButton(\n                      icon: const Icon(Icons.favorite_border, size: 18),\n                      onPressed: () {},\n                      padding: const EdgeInsets.all(4),\n                      constraints: const BoxConstraints(),\n                    ),\n                  ),\n                ),\n              ],\n            ),\n\n            // Product Details\n            Expanded(\n              child: Padding(\n                padding: const EdgeInsets.all(12),\n                child: Column(\n                  crossAxisAlignment: CrossAxisAlignment.start,\n                  children: [\n                    // Category Badge\n                    Container(\n                      padding: const EdgeInsets.symmetric(horizontal: 8, vertical: 2),\n                      decoration: BoxDecoration(\n                        color: Colors.teal.shade50,\n                        borderRadius: BorderRadius.circular(4),\n                      ),\n                      child: Text(\n                        product.category,\n                        style: TextStyle(\n                          fontSize: 10,\n                          color: Colors.teal.shade700,\n                          fontWeight: FontWeight.w600,\n                        ),\n                      ),\n                    ),\n                    const SizedBox(height: 6),\n\n                    // Product Name\n                    Text(\n                      product.name,\n                      style: const TextStyle(\n                        fontSize: 14,\n                        fontWeight: FontWeight.bold,\n                      ),\n                      maxLines: 2,\n                      overflow: TextOverflow.ellipsis,\n                    ),\n                    const SizedBox(height: 4),\n\n                    // Size and Finish\n                    Text(\n                      '${product.size} â€¢ ${product.finish}',\n                      style: TextStyle(\n                        fontSize: 11,\n                        color: Colors.grey[600],\n                      ),\n                    ),\n                    const Spacer(),\n\n                    // Rating\n                    Row(\n                      children: [\n                        Icon(Icons.star, size: 14, color: Colors.amber[700]),\n                        const SizedBox(width: 2),\n                        Text(\n                          product.rating.toString(),\n                          style: const TextStyle(\n                            fontSize: 12,\n                            fontWeight: FontWeight.w600,\n                          ),\n                        ),\n                        const SizedBox(width: 4),\n                        Text(\n                          '(${product.reviews})',\n                          style: TextStyle(\n                            fontSize: 11,\n                            color: Colors.grey[600],\n                          ),\n                        ),\n                      ],\n                    ),\n                    const SizedBox(height: 8),\n\n                    // Price and Add Button\n                    Row(\n                      mainAxisAlignment: MainAxisAlignment.spaceBetween,\n                      children: [\n                        Text(\n                          '\$${product.price.toStringAsFixed(2)}',\n                          style: const TextStyle(\n                            fontSize: 16,\n                            fontWeight: FontWeight.bold,\n                            color: Colors.teal,\n                          ),\n                        ),\n                        Container(\n                          decoration: BoxDecoration(\n                            color: product.inStock ? Colors.teal : Colors.grey,\n                            borderRadius: BorderRadius.circular(6),\n                          ),\n                          child: IconButton(\n                            icon: const Icon(Icons.add_shopping_cart, size: 18),\n                            color: Colors.white,\n                            onPressed: product.inStock\n                                ? () {\n                                    ScaffoldMessenger.of(context).showSnackBar(\n                                      SnackBar(\n                                        content: Text('${product.name} added to cart'),\n                                        duration: const Duration(seconds: 1),\n                                      ),\n                                    );\n                                  }\n                                : null,\n                            padding: const EdgeInsets.all(4),\n                            constraints: const BoxConstraints(),\n                          ),\n                        ),\n                      ],\n                    ),\n                  ],\n                ),\n              ),\n            ),\n          ],\n        ),\n      ),\n    );\n  }\n}\n